<?php//---------------------------------------------------------------------------------------------// Add a settings link to the WordPress top admin bar//---------------------------------------------------------------------------------------------add_action('admin_bar_menu', 'cap_free_ver_add_settings_link_to_admin_bar', 999);function cap_free_ver_add_settings_link_to_admin_bar($wp_admin_bar){    if (!current_user_can('manage_options')) return; //Premissions checker    $args = array(        'id'    => 'cap_free_ver_settings',        'title' => '<span class="ab-icon dashicons-cap_free_ver_icon_class"></span>'.__('Captcha free' ),        'href'  => admin_url('admin.php?page=cap_free_ver_options_page'),        'meta'  => array('title' => __('Captcha free'),)    );    $wp_admin_bar->add_node($args);}//---------------------------------------------------------------------------------------------//Add the plugin icon style to the top admin bar//---------------------------------------------------------------------------------------------add_action('wp_enqueue_scripts', 'cap_free_ver_admin_enqueue_style');add_action('admin_enqueue_scripts', 'cap_free_ver_admin_enqueue_style');function cap_free_ver_admin_enqueue_style() {    echo'    <style>        .dashicons-cap_free_ver_icon_class:before {            content: "\f113";            top: 3px;			color:darkorange !important;        }    </style>    ';}
?>